import React from 'react';
import Tile from './Tile';
import './Row.css';

function Row({ guess, solution, isSubmitted, shake }) {
  const tiles = [];

  for (let i = 0; i < 5; i++) {
    const letter = guess[i] || '';
    let state = '';

    if (isSubmitted && letter) {
      // Check if this is the winning guess (all letters match)
      const isWinningGuess = guess === solution;

      if (isWinningGuess) {
        // All tiles should be green/correct
        state = 'correct';
      } else if (solution[i] === letter) {
        state = 'correct';
      } else if (solution.includes(letter)) {
        state = 'present';
      } else {
        state = 'absent';
      }
    }

    tiles.push(
      <Tile 
        key={i} 
        letter={letter} 
        state={state}
      />
    );
  }

  return (
    <div className={`row ${shake ? 'shake' : ''}`}>
      {tiles}
    </div>
  );
}

export default Row;
