import React from 'react';
import Row from './Row';
import './Board.css';

function Board({ guesses, currentGuess, currentRow, solution, shake }) {
  return (
    <div className="board">
      {guesses.map((guess, i) => (
        <Row
          key={i}
          guess={i === currentRow ? currentGuess : guess}
          solution={solution}
          isSubmitted={i < currentRow}
          shake={shake && i === currentRow}
        />
      ))}
    </div>
  );
}

export default Board;
