import React, { useState, useEffect } from 'react';
import './App.css';
import Board from './components/Board';
import Keyboard from './components/Keyboard';
import Modal from './components/Modal';
import { WORDS, ANSWERS } from './data/words';

function App() {
  const [solution, setSolution] = useState('');
  const [guesses, setGuesses] = useState(Array(6).fill(''));
  const [currentGuess, setCurrentGuess] = useState('');
  const [currentRow, setCurrentRow] = useState(0);
  const [gameStatus, setGameStatus] = useState('playing'); // playing, won, lost
  const [letterStates, setLetterStates] = useState({});
  const [lockedLetters, setLockedLetters] = useState(new Set());
  const [shake, setShake] = useState(false);
  const [remainingWords, setRemainingWords] = useState(0);

  // Initialize game - use ANSWERS for the solution
  useEffect(() => {
    const randomAnswer = ANSWERS[Math.floor(Math.random() * ANSWERS.length)].toUpperCase();
    setSolution(randomAnswer);
  }, []);

  // Handle keyboard input
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (gameStatus !== 'playing') return;

      if (e.key === 'Enter') {
        handleSubmit();
      } else if (e.key === 'Backspace') {
        handleDelete();
      } else if (/^[a-zA-Z]$/.test(e.key)) {
        const upperKey = e.key.toUpperCase();
        // Prevent locked letters
        if (!lockedLetters.has(upperKey)) {
          handleLetter(upperKey);
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentGuess, currentRow, gameStatus, lockedLetters]);

  // Calculate remaining words whenever game state changes
  useEffect(() => {
    setRemainingWords(calculateRemainingWords());
  }, [lockedLetters, letterStates, solution]);

  const handleLetter = (letter) => {
    // Prevent using locked letters
    if (lockedLetters.has(letter)) {
      return;
    }

    if (currentGuess.length < 5) {
      setCurrentGuess(currentGuess + letter);
    }
  };

  const handleDelete = () => {
    setCurrentGuess(currentGuess.slice(0, -1));
  };

  const handleSubmit = () => {
    if (currentGuess.length !== 5) {
      setShake(true);
      setTimeout(() => setShake(false), 500);
      return;
    }

    // Check if word is valid using WORDS (full valid list)
    if (!WORDS.includes(currentGuess.toLowerCase())) {
      setShake(true);
      setTimeout(() => setShake(false), 500);
      return;
    }

    // Update guesses
    const newGuesses = [...guesses];
    newGuesses[currentRow] = currentGuess;
    setGuesses(newGuesses);

    // Update letter states for keyboard
    updateLetterStates(currentGuess);

    // Check win condition
    if (currentGuess === solution) {
      setGameStatus('won');
      return;
    }

    // Check lose condition
    if (currentRow === 5) {
      setGameStatus('lost');
      return;
    }

    // Move to next row
    setCurrentRow(currentRow + 1);
    setCurrentGuess('');
  };

  const updateLetterStates = (guess) => {
    const newStates = { ...letterStates };
    const newLocked = new Set(lockedLetters);

    for (let i = 0; i < guess.length; i++) {
      const letter = guess[i];

      if (solution[i] === letter) {
        newStates[letter] = 'correct';
      } else if (solution.includes(letter) && newStates[letter] !== 'correct') {
        newStates[letter] = 'present';
      } else if (!solution.includes(letter)) {
        newStates[letter] = 'absent';
        newLocked.add(letter); // Lock this letter
      }
    }

    setLetterStates(newStates);
    setLockedLetters(newLocked);
  };

  const calculateRemainingWords = () => {
    if (!solution) return ANSWERS.length;

    // Filter words based on what we know
    const possibleWords = ANSWERS.filter(word => {
      const upperWord = word.toUpperCase();

      // Check if word contains any locked letters
      for (let letter of lockedLetters) {
        if (upperWord.includes(letter)) {
          return false;
        }
      }

      // Check if word matches the letter states we know
      for (let letter in letterStates) {
        if (letterStates[letter] === 'correct') {
          // If we know a correct position, check if this word has it
          let hasCorrectPosition = false;
          for (let i = 0; i < solution.length; i++) {
            if (solution[i] === letter && upperWord[i] === letter) {
              hasCorrectPosition = true;
            }
          }
          if (!hasCorrectPosition) return false;
        }

        if (letterStates[letter] === 'present') {
          // Word must contain this letter
          if (!upperWord.includes(letter)) {
            return false;
          }
        }
      }

      return true;
    });

    return possibleWords.length;
  };

  const resetGame = () => {
    // Use ANSWERS for the solution (common words only)
    const randomAnswer = ANSWERS[Math.floor(Math.random() * ANSWERS.length)].toUpperCase();
    setSolution(randomAnswer);
    setGuesses(Array(6).fill(''));
    setCurrentGuess('');
    setCurrentRow(0);
    setGameStatus('playing');
    setLetterStates({});
    setLockedLetters(new Set());
  };

  return (
    <div className="App">
      <header className="header">
        <h1>WORDLE CLONE</h1>
      </header>

      <div className="game-container">
        {gameStatus === 'playing' && (
          <div className="word-counter">
            <span className="counter-label">Possible words remaining:</span>
            <span className="counter-number">{remainingWords}</span>
          </div>
        )}

        <Board 
          guesses={guesses}
          currentGuess={currentGuess}
          currentRow={currentRow}
          solution={solution}
          shake={shake}
        />

        <Keyboard 
          onKeyPress={handleLetter}
          onEnter={handleSubmit}
          onDelete={handleDelete}
          letterStates={letterStates}
          disabled={gameStatus !== 'playing'}
          lockedLetters={lockedLetters}
        />
      </div>

      {gameStatus !== 'playing' && (
        <Modal 
          isOpen={gameStatus !== 'playing'}
          gameStatus={gameStatus}
          solution={solution}
          guesses={guesses.filter(g => g !== '')}
          onPlayAgain={resetGame}
        />
      )}
    </div>
  );
}

export default App;
